INSERT INTO `reading_pharse_word_vocabularies` (`id`, `vocabulary_id`, `phrase_word`, `content`, `status`, `created_at`, `updated_at`) VALUES
(1, 1, 'a work of art/a work of music/a work of literature', '<p><strong>Meaning:</strong> a painting, sculpture, book, play, piece of music etc<br />\n<strong>Example:</strong> Shakespeare&rsquo;s plays are considered as one of the greatest works of<br />\nliterature in the English language.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA work a art/ music/ literature: một t&aacute;c phẩm hội họa/ &acirc;m nhạc/ văn học<br />\n<strong>Nghĩa:</strong> một bức họa, t&aacute;c phẩm đi&ecirc;u khắc, cuốn s&aacute;ch, vở kịch, đoạn nhạc&hellip;<br />\n<strong>V&iacute; dụ:</strong> Những vở kịch của Shakespeare được coi l&agrave; một trong những t&aacute;c phẩm<br />\nvăn học bằng tiếng Anh vĩ đại nhất.<br />\n&nbsp;</p>', 1, '2017-09-04 19:48:54', '2017-09-04 19:48:54'),
(2, 1, 'the aesthetic qualities of something', '<p><strong>Meaning: </strong>The qualities related to beauty and understanding the beauty of<br />\nsomething.<br />\n<strong>Example: </strong>One advantage of studying the arts is that students learn to appreciate<br />\nthe aesthetic qualities of paintings, literature and music.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nThe aesthetic qualities of something: chất lượng thẩm mỹ của c&aacute;i g&igrave;<br />\n<strong>Nghĩa:</strong> Những đặc trưng li&ecirc;n quan đến vẻ đẹp v&agrave; sự thấu hiểu vẻ đẹp của c&aacute;i g&igrave;<br />\n<strong>V&iacute; dụ:</strong> Một trong những ưu điểm của việc học nghệ thuật l&agrave; học sinh c&oacute; thể hiểu<br />\nv&agrave; tr&acirc;n trọng chất lượng thẩm mỹ của những bức họa, t&aacute;c phẩm văn học v&agrave; &acirc;m<br />\nnhạc.<br />\n&nbsp;</p>', 1, '2017-09-04 19:49:27', '2017-09-04 19:49:27'),
(4, 1, 'The portrayal of something/somebody', '<p><strong>Meaning:</strong> the act of showing or describing something/somebody in a book,<br />\npainting, play or film<br />\n<strong>Example</strong>: In the past, the portrayal of women in art was often as possessions,<br />\nowned by rich men.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nThe portrayal of something/ somebody: mi&ecirc;u tả ch&acirc;n dung/ khắc họa ch&acirc;n dung<br />\nai đ&oacute;<br />\n<strong>Nghĩa:</strong> việc thể hiện hoặc mi&ecirc;u tả ai/ c&aacute;i g&igrave; trong một cuốn s&aacute;ch, bức họa, vở kịch<br />\nhay phim ảnh.<br />\n<strong>V&iacute; dụ: </strong>Trước đ&acirc;y, việc khắc họa ch&acirc;n dung phụ nữ được coi l&agrave; đặc quyền của<br />\nngười gi&agrave;u.<br />\n&nbsp;</p>', 1, '2017-09-04 19:50:07', '2017-09-04 19:50:07'),
(5, 1, 'A private viewing', '<p><strong>Meaning:</strong> an occasion when a few people are invited to look at an exhibition of<br />\npaintings before it is open to the public.<br />\n<strong>Example:</strong> Paintings are sometimes considered as a form of investment, and art<br />\ndealers arrange private viewings for art collectors to purchase works of value.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA private viewing: đi xem trước triển l&atilde;m<br />\n<strong>Nghĩa:</strong> khi một v&agrave;i người được mời tới xem triển l&atilde;m hội họa trước khi n&oacute; mở cửa<br />\ncho c&ocirc;ng ch&uacute;ng.<br />\n<strong>V&iacute; dụ:</strong> Hội họa đ&ocirc;i khi được coi như một h&igrave;nh thức đầu tư, v&agrave; c&aacute;c nh&agrave; bu&ocirc;n b&aacute;n<br />\nnghệ thuật sắp xếp những buổi xem trước triển l&atilde;m để những nh&agrave; sưu tập tranh<br />\nc&oacute; thể xem x&eacute;t, định gi&aacute;<br />\n&nbsp;</p>', 1, '2017-09-04 19:50:52', '2017-09-04 19:50:52'),
(6, 1, 'To be a connoisseur of', '<p><strong>Meaning:</strong> an expert on matters involving the judgment of beauty, quality or skill<br />\nin art or music<br />\n<strong>Example:</strong> In order to enjoy beautiful paintings, it is not necessary to be a<br />\nconnoisseur of works of art.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo be a connoisseur of: người am hiểu c&aacute;i g&igrave; đ&oacute;<br />\n<strong>Nghĩa:</strong> một chuy&ecirc;n gia trong vi&ecirc;c đ&aacute;nh gi&aacute; vẻ đẹp, chất lượng v&agrave; kỹ thuật của<br />\nmột bức tranh/ bản nhạc.<br />\n<strong>V&iacute; dụ: </strong>Kh&ocirc;ng cần phải l&agrave; một người am hiểu nghệ thuật mới c&oacute; thể thưởng thức<br />\nvẻ đẹp của những bức họa.<br />\n&nbsp;</p>', 1, '2017-09-04 19:51:25', '2017-09-04 19:51:25'),
(7, 1, 'A priceless portrait', '<p><strong>Meaning:</strong> a painting of a person that is extremely valuable<br />\n<strong>Example: </strong>Private art collections may sometimes include priceless portraits of<br />\nkings or queens painted by famous artists.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA priceless portrait: bức ch&acirc;n dung v&ocirc; gi&aacute;<br />\n<strong>Nghĩa:</strong> bức họa một người n&agrave;o đ&oacute; m&agrave; v&ocirc; c&ugrave;ng gi&aacute; trị<br />\n<strong>V&iacute; dụ:</strong> Bộ sưu tập tranh c&aacute; nh&acirc;n đ&ocirc;i khi bao gồm cả những bức ch&acirc;n dung v&ocirc; gi&aacute;<br />\ncủa những vị vua v&agrave; ho&agrave;ng hậu được vẽ bởi những họa sĩ nổi tiếng<br />\n&nbsp;</p>', 1, '2017-09-04 19:52:01', '2017-09-04 19:52:01'),
(8, 2, 'To take something into consideration/ account', '<p><strong>Meaning: </strong>to consider or remember something when judging a situation<br />\n<strong>Example: </strong>No business will succeed if it fails to take the needs of customers into<br />\naccount/into consideration.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo take something into consideration/account: xem x&eacute;t c&aacute;i g&igrave;<br />\n<strong>Nghĩa:</strong> xem x&eacute;t hay nhớ tới c&aacute;i g&igrave; khi đ&aacute;nh gi&aacute; 1 vấn đề, 1 t&igrave;nh huống<br />\n<strong>V&iacute; dụ:</strong> Việc kinh doanh kh&ocirc;ng thể th&agrave;nh c&ocirc;ng nếu như kh&ocirc;ng xem x&eacute;t đến nhu<br />\ncầu của kh&aacute;ch h&agrave;ng.<br />\n&nbsp;</p>', 1, '2017-09-04 19:56:55', '2017-09-04 19:56:55'),
(9, 2, 'To break into a market', '<p><strong>Meaning:</strong> to enter a market with the connotation that entrance to the industry<br />\nwas difficult.<br />\n<strong>Example:</strong> It is difficult to break into the market for designer sportswear because<br />\nthere is too much competition from leading brands like Nike.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo break into a market: th&acirc;m nhập thị trường<br />\n<strong>Nghĩa:</strong> gia nhập v&agrave;o thị trường c&oacute; r&agrave;o cản gia nhập cao (kh&oacute; th&acirc;m nhập)<br />\n<strong>V&iacute; dụ: </strong>Rất kh&oacute; khăn để th&acirc;m nhập thị trường quần &aacute;o thể thao do sự cạnh tranh<br />\nmạnh từ c&aacute;c thương hiệu dẫn đầu như Nike.<br />\n&nbsp;</p>', 1, '2017-09-04 19:57:29', '2017-09-04 19:57:29'),
(10, 2, 'To keep someone’s word', '<p><strong>Meaning:</strong> to uphold one&#39;s promise; to do as one says<br />\n<strong>Example: </strong>I lent him a lot of money, so I hope that he keeps his word and repays<br />\nthe loan.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo keep someone&rsquo;s word: Giữ lời<br />\n<strong>Nghĩa:</strong> giữ lời hứa, l&agrave;m như những g&igrave; đ&atilde; n&oacute;i<br />\n<strong>V&iacute; dụ: </strong>T&ocirc;i cho anh ta vay rất nhiều tiền, n&ecirc;n t&ocirc;i hy vọng anh ta sẽ giữ lời v&agrave; ho&agrave;n<br />\ntrả khoản nợ đ&oacute; cho t&ocirc;i.<br />\n&nbsp;</p>', 1, '2017-09-04 19:58:51', '2017-09-04 19:58:51'),
(11, 2, 'To reach a consensus', '<p><strong>Meaning</strong>: to come to an agreement<br />\n<strong>Example</strong>: The directors of the company have reached a consensus on the new<br />\nbusiness plan.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo reach a consensus: đạt được thỏa thuận<br />\n<strong>Nghĩa:</strong> 2 b&ecirc;n c&ugrave;ng đồng &yacute; với một quyết định n&agrave;o đ&oacute;, đi tới 1 thỏa thuận<br />\n<strong>V&iacute; dụ:</strong> C&aacute;c gi&aacute;m đốc của c&ocirc;ng ty đ&atilde; đạt được thỏa thuận về kế hoạch kinh doanh<br />\nmới.<br />\n&nbsp;</p>', 1, '2017-09-04 19:59:50', '2017-09-04 19:59:50'),
(12, 2, 'To make the most of something', '<p><strong>Meaning:</strong> to make something appear as good as possible; to exploit something;<br />\nto get as much out of something as is possible.<br />\n<strong>Example:</strong> They designed the advertisements to make the most of the product&#39;s<br />\nfeatures.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo make the most of something: tận dụng c&aacute;i g&igrave;<br />\n<strong>Nghĩa</strong>: tạo ra c&aacute;i g&igrave; một c&aacute;ch tốt nhất c&aacute;i g&igrave; hoặc khai th&aacute;c c&aacute;i g&igrave; một c&aacute;ch triệt<br />\nđể.<br />\n<strong>V&iacute; dụ:</strong> Họ thiết kế những quảng c&aacute;o c&oacute; thể khai th&aacute;c tối đa c&aacute;c đặc trưng của sản<br />\nphẩm.<br />\n&nbsp;</p>', 1, '2017-09-04 20:00:27', '2017-09-04 20:00:27'),
(13, 3, 'To hide one’s light under a bushel', '<p><strong>Meaning:</strong> to conceal one&rsquo;s talents and skills<br />\n<strong>Example</strong>: Please don&#39;t hide your light under a bushel&mdash;the Society needs<br />\nyour valuable expertise.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo hide one&rsquo;s light under a bushel: giấu ta&igrave;<br />\n<strong>Nghĩa</strong>: che giấu t&agrave;i năng của ai đ&oacute;<br />\n<strong>V&iacute; dụ</strong>: Xin đừng che giấu t&agrave;i năng của anh &ndash; x&atilde; hội cần những kiến thức v&ocirc; gi&aacute;<br />\ncủa anh.<br />\n&nbsp;</p>', 1, '2017-09-04 20:02:13', '2017-09-04 20:02:13'),
(14, 3, 'To throw a tantrum', '<p><strong>Meaning</strong>: to become very angry and unreasonable<br />\n<strong>Example</strong>: When you are a grown-up, you don&#39;t throw a tantrum if something<br />\noffends you, you discuss it.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo throw a tantrum: tức giận v&ocirc; cớ<br />\n<strong>Nghĩa</strong>: cực kỳ tức giận 1 c&aacute;ch v&ocirc; l&yacute;<br />\n<strong>V&iacute; dụ: </strong>Khi đ&atilde; l&agrave; một người trưởng th&agrave;nh, bạn sẽ kh&ocirc;ng c&ograve;n tức giận v&ocirc; cớ nếu<br />\nc&oacute; g&igrave; kh&ocirc;ng vừa l&ograve;ng, m&agrave; sẽ thẳng thắn n&oacute;i về điều đ&oacute;<br />\n&nbsp;</p>', 1, '2017-09-04 20:02:51', '2017-09-04 20:02:51'),
(15, 3, 'To stamp one’s mark on sth', '<p><strong>Meaning</strong>: to affect or change something in a way that allows people to<br />\nrecognize one&rsquo;s influence<br />\n<strong>Example</strong>: She has stamped her own mark on the British film industry.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo stamp one&rsquo;s mark on sth: để lại dấu ấn c&aacute; nh&acirc;n<br />\n<strong>Nghĩa</strong>: c&oacute; ảnh hưởng/ thay đổi điều g&igrave; đ&oacute; khiến người kh&aacute;c phải c&ocirc;ng nhận<br />\nsự ảnh hưởng đ&oacute;.<br />\n<strong>V&iacute; dụ:</strong> C&ocirc; ấy đ&atilde; để lại dấu ấn c&aacute; nh&acirc;n trong ng&agrave;nh c&ocirc;ng nghiệp điện ảnh của<br />\nnước Anh.<br />\n&nbsp;</p>', 1, '2017-09-04 20:03:29', '2017-09-04 20:03:29'),
(16, 3, 'A clash of personalities', '<p><strong>Meaning</strong>: a situation in which two people disagree because they have very<br />\ndifferent personalities<br />\n<strong>Example</strong>: There had been a clash of personalities between the two leaders<br />\nover the question of defence policy<br />\n&nbsp;</p>\n\n<p><span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA clash of personalities: sự bất đồng về t&iacute;nh c&aacute;ch<br />\n<strong>Nghĩa</strong>: khi 2 người bất đồng với nhau do t&iacute;nh c&aacute;ch qu&aacute; tr&aacute;i ngược<br />\n<strong>V&iacute; dụ</strong>: Hai vị l&atilde;nh đạo c&oacute; sự tr&aacute;i ngược về t&iacute;nh c&aacute;ch khi n&oacute;i về chiến lược<br />\nph&ograve;ng thủ.<br />\n&nbsp;</p>', 1, '2017-09-04 20:04:05', '2017-09-04 20:04:05'),
(17, 3, 'To have a superiority complex', '<p><strong>Meaning</strong>: to have a feeling that one is superior to others<br />\n<strong>Example</strong>: She has a superiority complex, imagining that she is somehow<br />\nbetter at everything than her classmates.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo have a superiority complex: phức cảm tự t&ocirc;n (ngược với phức cảm tự ti &ndash;<br />\ninferiority complex)<br />\n<strong>Nghĩa</strong>: c&oacute; suy nghĩ, cảm x&uacute;c rằng m&igrave;nh vượt trội hơn người kh&aacute;c<br />\n<strong>V&iacute; dụ:</strong> C&ocirc; ấy c&oacute; phức cảm tự t&ocirc;n, ảo tưởng rằng c&ocirc; ấy tốt hơn về mọi mặt so<br />\nvới c&aacute;c bạn c&ugrave;ng lớp.<br />\n&nbsp;</p>', 1, '2017-09-04 20:04:34', '2017-09-04 20:04:34'),
(18, 4, 'Crime of passion', '<p><strong>Meaning</strong>: refers to a crime, especially murder, caused by sexual jealousy<br />\n<strong>Example</strong>: People who carry out crimes of passion often regret their actions<br />\nlater, when they reflect on what they have done.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nCrime of passion: đ&aacute;nh ghen, tội do ghen tu&ocirc;ng<br />\n<strong>Nghĩa</strong>: h&agrave;nh động phạm tội, đặc biệt l&agrave; giết người, xảy ra do ghen tu&ocirc;ng<br />\n<strong>V&iacute; dụ: </strong>Những người phạm tội do ghen tu&ocirc;ng sau đ&oacute; thường rất hối hận khi<br />\nnghĩ lại những g&igrave; đ&atilde; l&agrave;m.<br />\n&nbsp;</p>', 1, '2017-09-04 20:06:08', '2017-09-04 20:06:08'),
(20, 4, 'To serve a prison sentence', '<p><strong>Meaning</strong>: to carry out confinement in prison as a punishment<br />\n<strong>Example</strong>: They are serving long prison sentences for their part in the<br />\nassassination.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo serve a prison sentence: thi h&agrave;nh &aacute;n<br />\n<strong>Nghĩa</strong>: thực hiện h&igrave;nh phạt trong t&ugrave; do tội lỗi m&igrave;nh g&acirc;y ra<br />\n<strong>V&iacute; dụ</strong>: Họ phải chịu mức t&ugrave; phạt d&agrave;i v&igrave; tham gia v&agrave;o vụ &aacute;m s&aacute;t.<br />\n&nbsp;</p>', 1, '2017-09-04 20:06:45', '2017-09-04 20:06:45'),
(21, 4, 'To turn to crime/ drugs', '<p><strong>Meaning</strong>: To start committing crimes or using drugs.<br />\n<strong>Example</strong>: During periods of high youth unemployment, some youngsters turn<br />\nto crime to obtain money.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo turn to crime/ drugs: bắt đầu phạm tội/ d&ugrave;ng thuốc<br />\n<strong>Nghĩa</strong>: bắt đầu phạm tội<br />\n<strong>V&iacute; dụ</strong>: Trong thời gian c&ograve;n trẻ m&agrave; thất nghiệp, nhiều người trẻ bắt đầu dấn<br />\nth&acirc;n v&agrave;o con đường tội phạm để kiếm tiền.<br />\n&nbsp;</p>', 1, '2017-09-04 20:07:13', '2017-09-04 20:07:13'),
(22, 4, 'To be tough on crime', '<p><strong>Meaning</strong>: To punish crime severely<br />\n<strong>Example</strong>: The crime rate is likely to fall if governments take strong measures<br />\nto be tough on crime.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo be tough on crime: phạt nặng<br />\n<strong>Nghĩa</strong>: đưa ra h&igrave;nh phạt nghi&ecirc;m khắc với tội phạm<br />\n<strong>V&iacute; dụ:</strong> Tỉ lệ tội phạm chắc chắn sẽ giảm nếu ch&iacute;nh phủ c&oacute; những biện ph&aacute;p<br />\nmạnh tay v&agrave; đưa ra h&igrave;nh phạt nghi&ecirc;m khắc với những kẻ phạm tội.<br />\n&nbsp;</p>', 1, '2017-09-04 20:07:43', '2017-09-04 20:07:43'),
(23, 4, 'To contestthe verdict', '<p><strong>Meaning</strong>: not agree with the decision of the jury after the trial of a case<br />\n<strong>Example</strong>: In many countries, the accused person has the right to contest<br />\ntheverdict which was reached in the court.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo contest the verdict: phản đối quyết định của t&ograve;a &aacute;n/ chống &aacute;n<br />\n<strong>Nghĩa</strong>: kh&ocirc;ng đồng &yacute; với quyết định của t&ograve;a về vụ &aacute;n n&agrave;o đ&oacute;<br />\n<strong>V&iacute; dụ</strong>: Ở nhiều nước, bị c&aacute;o c&oacute; quyền phản đối quyết định của t&ograve;a khi đưa ra<br />\nx&eacute;t xử.<br />\n&nbsp;</p>', 1, '2017-09-04 20:08:08', '2017-09-04 20:08:08'),
(24, 1, 'To commission a work of art', '<p><strong>Meaning</strong>: to officially ask somebody to paint a picture or compose a piece of<br />\nmusic for you<br />\n<strong>Example</strong>: Artists in the past often relied on wealthy people to<br />\ncommissionpaintings or music from them.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo commission a work of art: đặt l&agrave;m/ mua một t&aacute;c phẩm nghệ thuật<br />\n<strong>Nghĩa</strong>: y&ecirc;u cầu ai đ&oacute; vẽ 1 bức tranh/ s&aacute;ng t&aacute;c 1 bản nhạc cho bạn<br />\n<strong>V&iacute; dụ</strong>: Nghệ sĩ trước kia thường tr&ocirc;ng chờ người gi&agrave;u c&oacute; đặt mua những bức<br />\ntranh hay bản nhạc của họ.<br />\n&nbsp;</p>', 1, '2017-09-04 22:11:50', '2017-09-04 22:11:50'),
(25, 1, 'An art movement', '<p><strong>Meaning</strong>: a group of artists who share similar ideas<br />\n<strong>Example</strong>: The creative aspect of art is demonstrated by the number of art<br />\nmovements over the centuries &ndash; including the Impressionists, the Romantics and<br />\nthe Modernists.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nAn art movement: trường ph&aacute;i nghệ thuật<br />\n<strong>Nghĩa</strong>: một nh&oacute;m c&aacute;c nghệ sĩ c&oacute; c&ugrave;ng chung &yacute; tưởng<br />\n<strong>V&iacute; dụ:</strong> Kh&iacute;a cạnh s&aacute;ng tạo của nghệ thuật được thể hiện bởi h&agrave;ng loạt những<br />\nphong tr&agrave;o nghệ thuật qua nhiều thế kỉ - bao gồm trường ph&aacute;i ấn tượng, chủ<br />\nnghĩa l&atilde;ng mạn v&agrave; chủ nghĩa hiện đại<br />\n&nbsp;</p>', 1, '2017-09-04 22:12:26', '2017-09-04 22:12:26'),
(26, 1, 'Performance art', '<p><strong>Meaning</strong>: A form of art in which the artist performs something, rather than<br />\nproducing a physical work of art<br />\n<strong>Example</strong>: Some tourist destinations offer performance art by actors to recreate<br />\nevents from local history, such as battles or local legends.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nPerformance art: nghệ thuật biểu diễn<br />\n<strong>Nghĩa</strong>: Một h&igrave;nh thức nghệ thuật m&agrave; ở đ&oacute; người nghệ sĩ biểu diễn c&aacute;i g&igrave; đ&oacute; chứ<br />\nkh&ocirc;ng sản xuất một t&aacute;c phẩm nghệ thuật<br />\n<strong>V&iacute; dụ</strong>: Một số điểm đến du lịch c&oacute; c&aacute;c buổi biểu diễn nghệ thuật để t&aacute;i tạo những<br />\nsự kiện lịch sử, như một trận chiến hay c&aacute;c huyền thoại tại địa phương<br />\n&nbsp;</p>', 1, '2017-09-04 22:13:08', '2017-09-04 22:13:08'),
(27, 1, 'Funding for the arts', '<p><strong>Meaning</strong>: the act of providing money for artists, writers, musicians etc<br />\n<strong>Example</strong>: Government funding for the arts encourages people of artistic ability<br />\nto express their talent to the public.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nFunding for the arts: quỹ nghệ thuật<br />\n<strong>Nghĩa</strong>: hoạt động g&acirc;y quỹ cho nghệ sĩ, nh&agrave; văn, nhạc sĩ,&hellip;<br />\n<strong>V&iacute; dụ</strong>: Quỹ nghệ thuật của nh&agrave; nước khuyến kh&iacute;ch người c&oacute; năng khiếu nghệ<br />\nthuật thể hiện t&agrave;i năng của họ tới c&ocirc;ng ch&uacute;ng.<br />\n&nbsp;</p>', 1, '2017-09-04 22:13:38', '2017-09-04 22:13:38'),
(28, 1, 'A mixed funding model for the arts', '<p><strong>Meaning</strong>: a model in which some money to support the arts is given by the<br />\ngovernment and some money is provided by private individuals or commercial<br />\norganizations.<br />\n<strong>Example</strong>: I would argue that a mixed funding model for the arts is the best way<br />\nto support artists, musicians and writers by combining public and provide funds<br />\nand even donations.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA mixed funding model for the arts: m&ocirc; h&igrave;nh quỹ nghệ thuật hỗn hợp<br />\n<strong>Nghĩa</strong>: m&ocirc; h&igrave;nh quỹ ủng hộ nghệ thuật do ch&iacute;nh phủ v&agrave; một số c&aacute; nh&acirc;n hay tổ<br />\nchức thương mại cung cấp, hỗ trợ.<br />\n<strong>V&iacute; dụ</strong>: T&ocirc;i cho rằng m&ocirc; h&igrave;nh quỹ nghệ thuật hỗn hợp l&agrave; c&aacute;ch tốt nhất để hỗ trợ<br />\nc&aacute;c nghệ sĩ, nhạc sĩ v&agrave; nhạc văn bằng c&aacute;ch kết hợp những quỹ chung v&agrave; quỹ tư,<br />\nthậm ch&iacute; l&agrave; quy&ecirc;n g&oacute;p.<br />\n&nbsp;</p>', 1, '2017-09-04 22:14:15', '2017-09-04 22:14:15'),
(29, 1, 'The imposition of censorship', '<p><strong>Meaning</strong>: the act of preventing something from being available to the public,<br />\nbecause it is considered offensive or a political threat<br />\n<strong>Example</strong>: I believe that the imposition of censorship is sometimes necessary to<br />\nprotect young people from exposure to pornographic writing or images.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nThe imposition of censorship: kiểm duyệt bắt buộc<br />\n<strong>Nghĩa</strong>: việc ngăn chặn c&aacute;i g&igrave; đ&oacute; được c&ocirc;ng bố tới c&ocirc;ng ch&uacute;ng v&igrave; n&oacute; c&oacute; thể nhạy<br />\ncảm hay đe dọa về mặt ch&iacute;nh trị<br />\n<strong>V&iacute; dụ</strong>: T&ocirc;i tin rằng kiểm duyệt bắt buộc c&oacute; những l&uacute;c l&agrave; cần thiết để bảo vệ thanh<br />\nthiếu ni&ecirc;n khỏi những h&igrave;nh ảnh đồi trụy.<br />\n&nbsp;</p>', 1, '2017-09-04 22:16:54', '2017-09-04 22:16:54'),
(30, 1, 'To stage a play', '<p><strong>Meaning</strong>: to organize and present a play for people to see<br />\n<strong>Example</strong>: It is important to encourage children to be creative, for example by<br />\nstaging a school play or displaying their artwork on school open days.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo stage a play: tr&igrave;nh diễn 1 vở kịch<br />\n<strong>Nghĩa</strong>: chuẩn bị v&agrave; biểu diễn 1 vở kịch cho mọi người c&ugrave;ng xem<br />\n<strong>V&iacute; dụ</strong>: Khuyến kh&iacute;ch trẻ em s&aacute;ng tạo l&agrave; rất quan trọng, v&iacute; dụ như tr&igrave;nh diễn 1 vở<br />\nkịch hay tr&igrave;nh b&agrave;y những t&aacute;c phẩm của ch&uacute;ng trong ng&agrave;y hội của trường.<br />\n&nbsp;</p>', 1, '2017-09-04 22:17:30', '2017-09-04 22:17:30'),
(31, 1, 'A talented playwright', '<p><strong>Meaning</strong>: a person who writes plays for the theatre<br />\n<strong>Example</strong>: Directors of theatres must encourage talented playwrights by staging<br />\ntheir plays for theatre-goers.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA talented playwright: nh&agrave; bi&ecirc;n kịch t&agrave;i năng<br />\n<strong>Nghĩa</strong>: người giỏi viết kịch cho nh&agrave; h&aacute;t<br />\n<strong>V&iacute; dụ</strong>: Những &ocirc;ng chủ nh&agrave; h&aacute;t cần khuyến kh&iacute;ch c&aacute;c nh&agrave; soạn kịch t&agrave;i năng bằng<br />\nc&aacute;ch tr&igrave;nh diễn vở kịch của họ cho kh&aacute;n giả tới xem.<br />\n&nbsp;</p>', 1, '2017-09-04 22:18:02', '2017-09-04 22:18:02'),
(32, 1, 'To appeal to audiences', '<p><strong>Meaning</strong>: to attract or interest those who go to watch a play or film.<br />\n<strong>Example</strong>: Plays which deal with contemporary social issues are likely to appeal to<br />\naudiences.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo appeal to audiences: hấp dẫn kh&aacute;n giả<br />\n<strong>Nghĩa</strong>: hấp dẫn người tới xem kịch hoặc phim<br />\n<strong>V&iacute; dụ</strong>: Những vở kịch n&oacute;i về c&aacute;c vấn đề đương thời thường hấp dẫn người xem.<br />\n&nbsp;</p>', 1, '2017-09-04 22:18:33', '2017-09-04 22:18:33'),
(33, 1, 'A literature festival/a book festival', '<p><strong>Meaning</strong>: A regular gathering of writers and readers in a particular city, perhaps<br />\nevery year.<br />\n<strong>Example</strong>: One way in which to attract tourists to a city is to stage an annual<br />\nliterature festival, and invite well-known writers to attend.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA literature festival: lễ hội văn học/ lễ hội s&aacute;ch<br />\n<strong>Nghĩa</strong>: Một buổi gặp mặt th&ocirc;ng thường giữa người viết v&agrave; người đọc trong một<br />\nth&agrave;nh phố, thường tổ chức h&agrave;ng năm.<br />\n<strong>V&iacute; dụ</strong>: Một c&aacute;ch thu h&uacute;t kh&aacute;ch du lịch tới th&agrave;nh phố l&agrave; tổ chức lễ hội văn học<br />\nh&agrave;ng năm, v&agrave; mời những nh&agrave; văn t&ecirc;n tuổi tới tham dự.<br />\n&nbsp;</p>', 1, '2017-09-04 22:20:26', '2017-09-04 22:20:26'),
(34, 1, 'Literary and artistic heritage', '<p><strong>Meaning</strong>: the traditon of writing or painting that a country, city or town has had<br />\nfor many years, distinguishing it as an important part of its character<br />\n<strong>Example</strong>: Visitors to Britain can enjoy the rich literary and artistic heritage by<br />\ngoing to such places as the birthplace of Shakespeare and the National Art<br />\nGallery in London.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nLiterary and artistic heritage: di sản văn h&oacute;a nghệ thuật<br />\n<strong>Nghĩa</strong>: Du kh&aacute;ch đến Anh c&oacute; thể thưởng thức di sản văn h&oacute;a nghệ thuật phong<br />\nph&uacute; bằng c&aacute;ch đến những điểm du lịch như nơi sinh của Shakespeare v&agrave; Viện<br />\ntriển l&atilde;m quốc gia tại London.<br />\n&nbsp;</p>', 1, '2017-09-04 22:21:11', '2017-09-04 22:21:11'),
(35, 1, 'To express oneself', '<p><strong>Meaning</strong>: to communicate some idea or emotion through speech, writing,<br />\npainting, music or some other form of art<br />\n<strong>Example</strong>: Schools should encourage pupils to express themselves through art,<br />\nmusic or creative writing in order to stimulate their imagination.<br />\n&nbsp;</p>\n\n<p><span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo express oneself: thể hiện bản th&acirc;n<br />\n<strong>Nghĩa</strong>: tr&igrave;nh b&agrave;y &yacute; tưởng/ cảm x&uacute; qua ph&aacute;t biểu, văn viết, hội họa, &acirc;m nhạc hay<br />\nnhững h&igrave;nh thức nghệ thuật kh&aacute;c.<br />\n<strong>V&iacute; dụ</strong>: Trường học n&ecirc;n khuyến kh&iacute;ch học sinh thể hiện bản th&acirc;n qua hội họa, &acirc;m<br />\nnhạc hay s&aacute;ng t&aacute;c văn học để k&iacute;ch th&iacute;ch tr&iacute; tưởng tượng của ch&uacute;ng.<br />\n&nbsp;</p>', 1, '2017-09-04 22:21:38', '2017-09-04 22:21:38'),
(36, 1, 'Works of literary fiction', '<p><strong>Meaning</strong>: types of literature which describe imaginary people and events, not real<br />\nones<br />\n<strong>Example</strong>: It takes enormous creativity to write works of literary fiction, and the<br />\ngreatest writers are rightly considered literary geniuses.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nWorks of literary fiction: t&aacute;c phẩm văn học giả tưởng<br />\n<strong>Nghĩa</strong>: thể loại văn học viết về những người v&agrave; sự kiện giả tưởng, kh&ocirc;ng c&oacute; thật<br />\n<strong>V&iacute; dụ</strong>: Phải s&aacute;ng tạo rất nhiều mới c&oacute; thể viết được những t&aacute;c phẩm văn học giả<br />\ntưởng, v&agrave; những t&aacute;c giả vĩ đại thường được coi l&agrave; những thi&ecirc;n t&agrave;i văn học thực<br />\nthụ<br />\n&nbsp;</p>', 1, '2017-09-04 22:22:07', '2017-09-04 22:22:07'),
(37, 1, 'A literary genius', '<p><strong>Meaning</strong>: a person with outstanding ability in writing<br />\n<strong>Example</strong>: Although not every child will become a literary genius, it is still<br />\nimportant for students to practice creative writing at school.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA literary genius: thi&ecirc;n t&agrave;i văn học<br />\n<strong>Nghĩa</strong>: người c&oacute; t&agrave;i năng văn học xuất ch&uacute;ng<br />\n<strong>V&iacute; dụ</strong>: D&ugrave; kh&ocirc;ng phải đứa trẻ n&agrave;o cũng sẽ l&agrave; thi&ecirc;n t&agrave;i văn học, nhưng việc cho<br />\nhọc sinh s&aacute;ng t&aacute;c văn học ở trường vẫn l&agrave; rất quan trọng<br />\n&nbsp;</p>', 1, '2017-09-04 22:22:34', '2017-09-04 22:22:34'),
(38, 1, 'Street musicians', '<p><strong>Meaning</strong>: people who play a musical instrument in the street to get money from<br />\npassers-by<br />\n<strong>Example</strong>: Street musicians are an entertaining feature of cultural life in many<br />\ncities.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nStreet musicians: nghệ sĩ đường phố<br />\n<strong>Nghĩa</strong>: những người chơi nhạc cụ tr&ecirc;n đường để xin tiền từ người qua đường<br />\n<strong>V&iacute; dụ</strong>: Nghệ sĩ đường phố l&agrave; một n&eacute;t đặc trưng văn h&oacute;a ở nhiều th&agrave;nh phố.<br />\n&nbsp;</p>', 1, '2017-09-04 22:23:08', '2017-09-04 22:23:08'),
(39, 1, 'Genres of music', '<p><strong>Meaning</strong>: particular types or styles of music<br />\n<strong>Example</strong>: Different genres of music tend to be favoured by different age groups,<br />\nwith classical music more popular among the elderly.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nGenres of music: thể loại &acirc;m nhạc<br />\n<strong>Nghĩa</strong>: những thể loại hay phong c&aacute;ch &acirc;m nhạc cụ thể<br />\n<strong>V&iacute; dụ</strong>: Những thể loại &acirc;m nhạc kh&aacute;c nhau được y&ecirc;u th&iacute;ch bởi những lứa tuổi kh&aacute;c<br />\nnhau, như l&agrave; nhạc cố điển th&igrave; được người cao tuổi ưa th&iacute;ch hơn.<br />\n&nbsp;</p>', 1, '2017-09-04 22:24:04', '2017-09-04 22:24:04'),
(40, 1, 'To take up a musical instrument', '<p><strong>Meaning</strong>: to start to learn to play a musical instrument<br />\n<strong>Example</strong>: Schools should encourage all those children who have an interest to<br />\ntake up a musical instrument.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo take up a musical instrument: học chơi một nhạc cụ<br />\n<strong>Nghĩa</strong>: bắt đầu học để chơi được một nhạc cụ n&agrave;o đ&oacute;<br />\n<strong>V&iacute; dụ</strong>: Trường học n&ecirc;n khuyến kh&iacute;ch những học sinh m&agrave; c&oacute; hứng th&uacute; chơi nhạc<br />\ncụ<br />\n&nbsp;</p>', 1, '2017-09-04 22:24:46', '2017-09-04 22:24:46'),
(41, 1, 'Live music', '<p><strong>Meaning</strong>: music that is not recorded &ndash; it is performed at the same time as people<br />\nlisten to it<br />\n<strong>Example</strong>: Without funding from governments, it is doubtful whether live music<br />\nwould survive.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nLive music: nhạc sống<br />\n<strong>Nghĩa</strong>: kh&ocirc;ng phải bản nhạc thu &acirc;m m&agrave; l&agrave; nhạc được biểu diễn trực tiếp cho<br />\nngười nghe.<br />\n<strong>V&iacute; dụ</strong>: Kh&ocirc;ng c&oacute; hỗ trợ của nh&agrave; nước, kh&ocirc;ng r&otilde; nhạc sống c&oacute; thể tiếp tục tồn tại<br />\nđược hay kh&ocirc;ng<br />\n&nbsp;</p>', 1, '2017-09-04 22:25:22', '2017-09-04 22:25:22'),
(42, 1, 'A concert venue', '<p><strong>Meaning</strong>: a place where people go to listen to live music at an organized event<br />\n<strong>Example</strong>: Cities offer many cultural attractions for music-lovers, such as the Paris<br />\nOpera.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA concert venue: nh&agrave; h&aacute;t<br />\n<strong>Nghĩa</strong>: nơi người ta đến nghe nhạc sống tại một sự kiện n&agrave;o đ&oacute;<br />\n&nbsp;</p>', 1, '2017-09-04 22:25:52', '2017-09-04 22:25:52'),
(43, 1, 'A resident orchestra', '<p><strong>Meaning</strong>: a large group of people who play musical instruments together in the<br />\nsame venue.<br />\n<strong>Example</strong>: In most cities, a resident orchestra must receive a subsidy from the<br />\nlocal authority in order to survive financially.<br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nA resident orchestra: một d&agrave;n giao hưởng<br />\n<strong>Nghĩa</strong>: một nh&oacute;m đ&ocirc;ng người c&ugrave;ng chơi nhạc cụ tại c&ugrave;ng một nơi<br />\n&nbsp;</p>', 1, '2017-09-04 22:26:19', '2017-09-04 22:26:19'),
(44, 1, 'An opera house', '<p><strong>Meaning</strong>: a dedicated building such as a theatre or other venue, in which the<br />\nperformers sing while the orchestra plays the music<br />\n<strong>Example</strong>: The financial costs of running an opera house are very high, and so<br />\nticket prices to see an opera are expensive.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nAn opera house: nh&agrave; h&aacute;t<br />\n<strong>Nghĩa</strong>: một t&ograve;a nh&agrave; được x&acirc;y như rạp h&aacute;t, nơi c&aacute;c nghệ sĩ h&aacute;t v&agrave; d&agrave;n giao hưởng<br />\nchơi nhạc.<br />\n<strong>V&iacute; dụ</strong>: Chi ph&iacute; t&agrave;i ch&iacute;nh để duy tr&igrave; một nh&agrave; h&aacute;t l&agrave; rất cao, n&ecirc;n gi&aacute; nghe nhạc giao<br />\nhưởng l&agrave; rất đắt.<br />\n&nbsp;</p>', 1, '2017-09-04 22:26:54', '2017-09-04 22:26:54'),
(45, 1, 'Folk songs', '<p><strong>Meaning</strong>: songs in the traditional style of a country or community<br />\n<strong>Example</strong>: Traditional folk songs are part of the cultural heritage of a nation or<br />\nregion.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nFolk songs: nhạc cổ truyền/ d&acirc;n tộc<br />\n<strong>Nghĩa</strong>: những b&agrave;i h&aacute;t theo phong c&aacute;ch truyền thống của một đất nước hay một<br />\ncộng đồng.<br />\n<strong>V&iacute; dụ</strong>: những b&agrave;i h&aacute;t d&acirc;n tộc cổ truyền l&agrave; một phần di sản văn h&oacute;a của một quốc<br />\ngia hay một v&ugrave;ng miền.<br />\n&nbsp;</p>', 1, '2017-09-04 22:33:06', '2017-09-04 22:33:06'),
(46, 1, 'Popular music', '<p><strong>Meaning</strong>: music of the type which is listened to by a large number of people, with<br />\na strong rhythm and simple tunes.<br />\n<strong>Example</strong>: It would be a pity if young people only listened to popular/pop music<br />\nand had no knowledge of traditional folk songs.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nPopular music: nhạc hiện đại<br />\n<strong>Nghĩa</strong>: thể loại được đa số mọi người nghe theo, với nhạc điệu mạnh mẽ v&agrave; c&aacute;c<br />\n&acirc;m đơn giản.<br />\n<strong>V&iacute; dụ:</strong> Sẽ rất đ&aacute;ng tiếc nếu người trẻ chỉ nghe nhạc hiện đại m&agrave; kh&ocirc;ng biết g&igrave; về<br />\nnhạc d&acirc;n gian cổ truyền.<br />\n&nbsp;</p>', 1, '2017-09-04 22:33:35', '2017-09-04 22:33:35'),
(47, 1, 'To sing in a choir', '<p><strong>Meaning</strong>: to sing in a group of people, for example in a church or public<br />\nperformance<br />\n<strong>Example</strong>: One way that schools can encourage children to enjoy music is to give<br />\nthem an opportunity to sing in a school choir.</p>\n\n<p><br />\n<span style=\"color:#c0392b;\"><strong>Dịch đại &yacute;</strong></span><br />\nTo sing in a choir: h&aacute;t đồng ca<br />\n<strong>Nghĩa</strong>: h&aacute;t theo 1 nh&oacute;m, v&iacute; dụ như trong nh&agrave; thờ hoặc buổi biểu diễn trước c&ocirc;ng<br />\nch&uacute;ng.<br />\n<strong>V&iacute; dụ</strong>: Một phương ph&aacute;p để nh&agrave; trường khuyến kh&iacute;ch trẻ thưởng thức &acirc;m nhạc l&agrave;<br />\ncho ch&uacute;ng cơ hội được h&aacute;t đồng ca<br />\n&nbsp;</p>', 1, '2017-09-04 22:34:14', '2017-09-04 22:34:14');
